import { Component } from '@angular/core';
import { AlertController, NavController } from 'ionic-angular';
import { Camera } from 'ionic-native';
import { DomSanitizer } from '@angular/platform-browser';

import { Database, Patient } from '../../database';
import { EnvironmentsPage } from '../environments/environments';

@Component({
  selector: 'page-patients',
  templateUrl: 'patients.html'
})

export class PatientsPage {

  //base64Image is the picture of the patient
  public base64Image: string;
  public imageData: string;
  patient: Patient;
  patientsList: Array<Patient>;
  addPatientBtn: boolean = false;

  constructor(
    private navCtrl: NavController,
    private alertCtrl: AlertController,
    public database: Database,
    private sanitizer: DomSanitizer
  ) {
    this.sanitizer=sanitizer;
  }

  public ionViewDidLoad() {
    this.updatePatientList();
  }

  public updatePatientList() {
    this.database.getPatients().then( data => {
      this.patientsList = data;
      if (this.patientsList.length < 1) {
        this.addPatientBtn = true;
      }
    })
  }

  public addPatient(patient: Patient) {

    let prompt = this.alertCtrl.create({
      title: 'Add Patient',
      inputs: [
        {
          name: 'name',
          placeholder: 'Enter patient\'s name'
        }
      ],
      buttons: [
        {
          text: 'Take a photo',
          handler: data => {

            // Camera part photo will be writen to imageData
            Camera.getPicture({
            destinationType: Camera.DestinationType.DATA_URL,
            encodingType: Camera.EncodingType.JPEG,
            targetWidth: 100,
            targetHeight: 100 }).then((imageData) => {

              // imageData is a base64 encoded string
              // this.base64Image = "data:image/jpeg;base64," + imageData;
              console.log(imageData);

              let patient = new Patient(null,data.name,imageData);
              console.log(patient);

              //Add to Database Patients table
              this.database.addpatientwithpicture(patient).then(response => {
                this.patientsList.push( data );
                this.updatePatientList();
              }).catch( error => {
                console.error( error );
              });
            });
          }
        },
        {
          text: 'Add without photo',
          handler: data => {

            //No Picture
            this.base64Image = "";
            let patient = new Patient(null,data.name,this.base64Image);
            this.database.addpatient(patient)
            this.updatePatientList();
          }
        },
        {
          text: 'Cancel'
        }
      ]
    });

    prompt.present();
  }

  public editPatient(patient: Patient) {

    let prompt = this.alertCtrl.create({
      title: 'Edit Patient',
      inputs: [
        {
          name: 'name',
          value: `${patient.name}`
        }
      ],
      buttons: [
        {
          text: 'Cancel'
        },
        {
          text: 'Save',
          handler: data => {
            patient.name = data.name;
            this.database.editPatient(patient);
            (error) => {
              console.log(error);
            }
            this.updatePatientList();
          }
        }
      ]
    });

    prompt.present();

  }

  public deletePatient(patient: Patient) {

    this.database.obliteratepatients(patient)

    let index = this.patientsList.indexOf(patient);

    if (index > -1) {
      this.patientsList.splice(index, 1);
    }
  }

  public openPage(patient: Patient) {

    this.navCtrl.push( EnvironmentsPage, { patient } );
  }
}
